const { createClient } = require(''@supabase/supabase-js'');
const supabase = createClient(process.env.NEXT_PUBLIC_SUPABASE_URL, process.env.SUPABASE_SERVICE_ROLE_KEY);

async function analyzeMassiveTradeFlows() {
  console.log(''üìä ANALYZING THE 597K+ TRADE FLOWS TABLE...\n'');

  try {
    // Get total count and basic stats
    const {data: countResult} = await supabase
      .from(''trade_flows'')
      .select(''*'', { count: ''exact'' })
      .limit(0);

    console.log(''üåç SCALE: '' + (countResult?.length || ''Unknown'') + ''+ total trade flow records'');

    // Sample some records to understand structure
    const {data: sampleRecords} = await supabase
      .from(''trade_flows'')
      .select(''*'')
      .limit(5);

    if (sampleRecords && sampleRecords.length > 0) {
      console.log(''\nüìã SAMPLE RECORD STRUCTURE:'');
      const sample = sampleRecords[0];
      Object.keys(sample).forEach(key => {
        console.log(''  '' + key + '': '' + (sample[key] || ''null''));
      });
    }

    // Analyze country coverage
    const {data: countries} = await supabase
      .from(''trade_flows'') 
      .select(''reporter_code, reporter_name'')
      .not(''reporter_code'', ''is'', null)
      .limit(1000);

    if (countries) {
      const uniqueCountries = [...new Set(countries.map(c => c.reporter_code))];
      console.log(''\nüåé COUNTRY COVERAGE: '' + uniqueCountries.length + ''+ countries represented'');
      console.log(''Top countries: '' + uniqueCountries.slice(0, 10).join('', ''));
    }

    // Analyze trade value ranges
    const {data: valueAnalysis} = await supabase
      .from(''trade_flows'')
      .select(''trade_value_usd'')
      .not(''trade_value_usd'', ''is'', null)
      .order(''trade_value_usd'', { ascending: false })
      .limit(10);

    if (valueAnalysis && valueAnalysis.length > 0) {
      console.log(''\nüí∞ TOP TRADE VALUES:'');
      valueAnalysis.forEach((record, i) => {
        const value = record.trade_value_usd;
        const formatted = value ? ''$'' + (value / 1000000).toFixed(1) + ''M'' : ''N/A'';
        console.log(''  #'' + (i + 1) + '': '' + formatted);
      });
    }

    // Check for automotive data in the massive dataset
    const {data: automotiveFlows} = await supabase
      .from(''trade_flows'')
      .select(''hs_code, product_description, trade_value_usd, reporter_name, partner_name'')
      .like(''hs_code'', ''87%'')
      .not(''trade_value_usd'', ''is'', null)
      .order(''trade_value_usd'', { ascending: false })
      .limit(10);

    if (automotiveFlows && automotiveFlows.length > 0) {
      console.log(''\nüöó AUTOMOTIVE TRADE FLOWS (87xx codes):'');
      automotiveFlows.forEach(flow => {
        const value = flow.trade_value_usd ? ''$'' + (flow.trade_value_usd / 1000000).toFixed(1) + ''M'' : ''N/A'';
        console.log(''  '' + flow.hs_code + '': '' + value + '' ('' + flow.reporter_name + '' ‚Üí '' + flow.partner_name + '')'');
      });
    }

  } catch (error) {
    console.error(''‚ùå Error analyzing trade flows:'', error);
  }
}

analyzeMassiveTradeFlows();
")

