async function analyzeDescriptionProblem() {
  console.log(''üîç ANALYZING THE DESCRIPTION PROBLEM IN 597K+ TRADE FLOWS...\n'');

  // Check description quality
  const {data: descriptions} = await supabase
    .from(''trade_flows'')
    .select(''hs_code, product_description'')
    .limit(20);

  console.log(''üìã SAMPLE PRODUCT DESCRIPTIONS:'');
  descriptions.forEach((item, i) => {
    console.log(''  '' + (i+1) + ''. '' + item.hs_code + '': ""'' + (item.product_description || ''NULL'') + ''""'');
