    .from(''comtrade_reference'')
    .select(''hs_code, product_description'')
    .like(''hs_code'', ''87%'')
    .limit(5);

  console.log(''\nüéØ CONTRAST: comtrade_reference HAS good descriptions:'');
  comtradeGood.forEach((item, i) => {
    console.log(''  '' + (i+1) + ''. '' + item.hs_code + '': ""'' + item.product_description + ''""'');
  });

  console.log(''\nüí° THE SOLUTION:'');
  console.log(''  ‚Ä¢ trade_flows = MASSIVE trade intelligence (597K+ flows)'');
  console.log(''  ‚Ä¢ comtrade_reference = DETAILED product descriptions (5.6K codes)'');
  console.log(''  ‚Ä¢ HYBRID APPROACH = Join both tables for complete intelligence!'');
}

analyzeDescriptionProblem();
