const supabase = createClient(process.env.NEXT_PUBLIC_SUPABASE_URL, process.env.SUPABASE_SERVICE_ROLE_KEY);

async function addJapanToCountries() {
  console.log(''üáØüáµ Adding Japan to countries database...'');

  const japanData = {
    code: ''JP'',
    name: ''Japan''
  };

  try {
    const { data, error } = await supabase
      .from(''countries'')
      .upsert(japanData, { onConflict: ''code'' });

    if (error) {
      console.error(''Error adding Japan:'', error);
    } else {
      console.log(''‚úÖ Added: JP - Japan'');
    }

    // Also check what countries we have now
    const { data: allCountries, error: listError } = await supabase
      .from(''countries'')
